#ifndef LLVM_LIB_TARGET_RISCIV_RISCIVREGISTERBANKINFO_H
#define LLVM_LIB_TARGET_RISCIV_RISCIVREGISTERBANKINFO_H

#include "llvm/CodeGen/GlobalISel/RegisterBankInfo.h"

#define GET_REGBANK_DECLARATIONS
#include "RISCIVGenRegisterBank.inc"

namespace llvm {

class TargetRegisterInfo;

class RISCIVGenRegisterBankInfo : public RegisterBankInfo {
protected:
#define GET_TARGET_REGBANK_CLASS
#include "RISCIVGenRegisterBank.inc"
};

/// This class provides the information for the target register banks.
class RISCIVRegisterBankInfo final : public RISCIVGenRegisterBankInfo {
public:
  RISCIVRegisterBankInfo(const TargetRegisterInfo &TRI);
};
} // end namespace llvm
#endif
